import React from "react";
import Block from "./components/Block/block";
import CustomInput from "./components/CustomInput/index";

import './App.css';

class Main extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            likes: 0,
            step: 10,
            move: 0,
            text: ''
        }
        this.changeLikes = this.changeLikes.bind(this);
        this.onMove = this.onMove.bind(this)
        this.changeText = this.changeText.bind(this)
    }
    changeLikes(step){
        this.setState({
            likes: this.state.likes + step
        })
    }
    onMove(){
        const {move, step} = this.state
        this.setState({
            move: move + step
        })
    }
    changeText(e){
        console.log(e.target.value);
        this.setState({
            text: e.target.value
        })
    }
    render() {
        const {likes, move, text} = this.state
        return (
            <div>
                <input type="text" value={text} onChange={(e) => this.changeText(e)} />
                <p>{likes}</p>
                <Block move={move}/>
                <button onClick={(step) => this.changeLikes(10)}>touch me!</button>
                <button onClick={this.onMove}>touch me!</button>
            </div>
        )
    }
}

export default Main;

// CustomInput.tsx       // Форма для добавления новой задачи
//     Task.tsx        // Задача
//     List.tsx        // Список задач
