import React from "react";
import CustomInput from "./components/CustomInput/index";
import List from "./components/List/index";
import Task from "./components/Task/index";

import './App.css';


class Main extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            tasks: [
                {id: 0, title: 'task1', done: false},
                {id: 1, title: 'task2', done: true},
                {id: 2, title: 'task3', done: false}
            ],
            newTask: ''
        }
        this.addTask = this.addTask.bind(this)
        this.changeInput = this.changeInput.bind(this)
    }
    componentDidMount() {
        fetch('https://fakestoreapi.com/products')
            .then(res=>res.json())
            .then(json=>{
                this.setState({
                    products: json
                })
            })
    }

    addTask() { 
        const {tasks, newTask} = this.state;
        newTask && this.setState({
            tasks: [...this.state.tasks, {id: tasks.length ,
                title: newTask,
                done: false}],
            newTask: ''
            }) 
    }
    changeInput(e){
        this.setState({
            newTask: e.target.value 
        })
        console.log(this.state.newTask);  
    }
    render() {
        const {products} = this.state;
        return (
            <div>
                {products.map((item) => <div key={item.id}>
                    <h3>{item.title}</h3>
                </div>
                )
                }

            </div>
        )
    }
}

export default Main;

// CustomInput.tsx       // Форма для добавления новой задачи
//     Task.tsx        // Задача
//     List.tsx        // Список задач
