import React from "react";
import Block from "./components/Block/block";
import CustomInput from "./components/CustomInput/index";
import Task from "./components/Task/index";

import './App.css';

class Main extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            tasks: [
                {id: 0, title: 'task1', done: false},
                {id: 1, title: 'task2', done: true},
                {id: 2, title: 'task3', done: false}
            ]
        }
        this.addTask = this.addTask.bind(this)

    }
    addTask(e) {

        this.setState(state => {
            let {tasks} = state;
            console.log(e);
             tasks.push({
                id: tasks.length,
                title: e.target.value,
                done: false
            })
            console.log(tasks.length);
       })
    }
    render() {
        const {tasks} = this.state
        return (
            <div>
                <div>
                    <h1>Active tasks: {tasks.length}</h1>
                </div>
                {tasks.map(task => <Task task={task}/>)
                }
                <CustomInput change={(e) => this.addTask(e)}/>
                {/* <input type="text" value={text} onChange={(e) => this.changeText(e)} />
