import { findByLabelText } from "@testing-library/react";
import React from "react";
import './index.css'

class List extends React.Component{
    constructor(props){
        super(props)
    }
    render() {
        const {task} = this.props;
        const style = {
            display: 'flex',
            alignItems: 'center'
        }
        return(
            <div style={style}>
                <p>{1+task.id}.   {task.title}     </p>
                {task.done && <div> &#9989; </div>}
            </div>
        )
    }
}
export default List;
