import React from "react";
import './index.css'

class CustomInput extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            input: ''
        };
    }
    changeInput(e){
        this.setState({
            input: e.target.value
        })
    }
    render() {
        const {change} = this.props;
        const {input} = this.state;
        return(
            <>
            <input type="text" value={input} onChange={this.changeInput(e)}></input>
            <button  onClick={this.change}>ADD task</button>
            </>
        )
    }
}
export default CustomInput;
