import React, { useEffect } from "react";
import {useState}  from "react";
import CustomInput from "./components/CustomInput/index";
import List from "./components/List/index";
import Task from "./components/Task/index";

import './App.css';

const Main = () => {
    const [tasks, setTasks] = useState([]);
    // const [tasks, saveTask] = useState([]);
    // const [user, setUser] = useState();
    // const [users, saveUser] = useState([]);

    const saveTask = (userInput) => {
        if (userInput) {
            const newTask = {
                id: Math.random().toString(36).substr(2,9),
                task: userInput.userInput,
                check: false
            }
            setTasks([...tasks, newTask])
        }

        console.log(tasks, userInput)
    }
    const removeTask = (delTask) => {
        setTasks([...tasks.filter((delTask) => delTask.id !== id)])
    }
    console.log(tasks)
    // const saveUser = useState();
    // useEffect(() => {
    //     //user && localStorage.setItem(user,[]);
    //     let arr = JSON.parse(localStorage.getItem(user));
    //     console.log(arr);
    //     arr.push({tasks});
    //     localStorage.setItem(user, JSON.stringify(arr))
    // }, [tasks, users]);


    return (
        <div className="main">
            <h1>ToDoList: {tasks.length}</h1>
            <CustomInput     
                saveTask={saveTask}/>   
            {tasks.map((el) => {
                console.log(el)
                return (
                    <List 
                        todo={el}
                        key={el.id}
                        removeTask={removeTask(tasks)}
                    />
                )
            })}


        </div>
    )
}



export default Main;

