import React, { useEffect } from "react";
import {useState}  from "react";
import CustomInput from "./components/CustomInput/index";
import List from "./components/List/index";
import UsernameInput from "./components/UsernameInput/index";

import './App.css';

const Main = () => {
    const [tasks, setTasks] = useState([]);
    const [user, setUser] = useState([Object.keys(localStorage)]);
    const [currentUser, setCurrentUser] = useState();
    //username input
    const inputnameUser = (username) => {
            setCurrentUser(username)
            getStor(user, username)
            console.log(user)
            //setUser(['2rf', '23t'])
            //console.log(JSON.parse(localStorage))
        if (!user.includes(username)) {
            setUser([...user, username])
            localStorage.setItem(username, "[]")
            console.log(user)
        } else {
            let taskUser = JSON.parse(localStorage.getItem(username))
            //setTasks(taskUser);
            console.log(username, 'taskk', tasks,  'i eto', taskUser)
        }
    }
    //dellete task
    const removeTask = (delTask) => {
        setTasks([...tasks.filter((el) => el.id !== delTask)])
    }
    //done task
    const toggleTask = (id) => {
        setTasks([
            ...tasks.map((el) => 
                el.id === id ? {...el, check: !el.check} : {...el})])
    }
    const getStor = (user, username) => {
        // (Object.keys(localStorage)).map((e) => {
        //     user.push(e)
        // })
        setUser([Object.keys(localStorage), username])
        // user.concat((Object.keys(localStorage)))
        // console.log(Object.keys(localStorage))
        console.log( Object.keys(localStorage))
    }
    useEffect(() => {
        console.log('1')
        if (user.includes(currentUser)) {
            setTasks(JSON.parse(localStorage.getItem(currentUser)))
        } else {
            //localStorage.setItem(currentUser, "[]");
        }
    }, [user, currentUser]);


    return (
        <div className="main">
            <UsernameInput               
                inputnameUser = {inputnameUser}
            />
            <h1>ToDoList: {tasks.length}</h1>
            <CustomInput     
                tasks={tasks}
                setTasks = {setTasks}
                currentUser = {currentUser}
                />   
                {tasks.map((el) => {
                console.log(el)
                return (
                    <List 
                        todo={el}
                        key={el.id}
                        removeTask={removeTask}
                        toggleTask = {toggleTask}
                    />
                )
            })}
        </div>
    )
}



export default Main;

// CustomInput.tsx       // Форма для добавления новой задачи
