import React, { useEffect } from "react";
import {useState}  from "react";
import CustomInput from "./components/CustomInput/index";
import List from "./components/List/index";
import UsernameInput from "./components/UsernameInput/index";

import './App.css';

const Main = () => {
    const [tasks, setTasks] = useState([]);
    const [user, setUser] = useState([])
    // const [tasks, saveTask] = useState([]);
    // const [user, setUser] = useState();
    // const [users, saveUser] = useState([]);
    const inputnameUser = (user) => {
        // const arr = JSON.parse(localStorage.getItem({user}))
        // console.log(arr)
        if (user) {

            console.log(user)
        }  else {
            localStorage.setItem(user,[])
            //console.log(user)
        }
    }
    const saveTask = (userInput) => {
        if (userInput) {
            const newTask = {
                id: Math.random().toString(36).substr(2,9),
                task: userInput.userInput,
                check: false
            }
            setTasks([...tasks, newTask])
        }
    }
    const removeTask = (delTask) => {
        setTasks([...tasks.filter((el) => el.id !== delTask)])
    }

    //console.log(tasks)
    // const saveUser = useState();
    // useEffect(() => {
    //     //user && localStorage.setItem(user,[]);
    //     let arr = JSON.parse(localStorage.getItem(user));
    //     console.log(arr);
    //     arr.push({tasks});
    //     localStorage.setItem(user, JSON.stringify(arr))
    // }, [tasks, users]);


    return (
        <div className="main">
            <UsernameInput 
                user = {user}
                setUser = {setUser}
                inputnameUser = {inputnameUser}
            />
            <h1>ToDoList: {tasks.length}</h1>
            <CustomInput     
                saveTask={saveTask}/>   
                {tasks.map((el) => {
                console.log(el)
                return (
                    <List 
                        todo={el}
                        key={el.id}
                        removeTask={removeTask}
                    />
                )
            })}


        </div>
    )
}



export default Main;

