import { findByLabelText } from "@testing-library/react";
import React from "react";

import './index.css'

const List = (tasks, removeTask, toggleTask) => {

    return (
        <div className="item-todo" key={tasks.id}>
            <div>{tasks.task}</div>
            <div onClick={() => toggleTask()} className="item-done">OK</div>
            <div onClick={() => removeTask(tasks.task)} className="item-delete">X</div>
        </div>
    )
}
export default List;
