import React from "react";
import './index.css'

const Task = ({user, saveUser, change}) => {

    return (
        <div className="main-form">
            <input type="text" value={user} onChange={(e) => change(e.target.value)} />
            <button onClick={() => saveUser({user})}>ADD</button>
        </div>
    )
}
export default Task;
