import React, { useEffect } from "react";
import {useState}  from "react";
import CustomInput from "./components/CustomInput/index";
// import List from "./components/List/index";
import Task from "./components/Task/index";

import './App.css';

const Main = () => {
    const [task, setTask] = useState();
    const [tasks, saveTask] = useState([]);
    const [user, setUser] = useState();
    const [users, saveUser] = useState([]);
    // const saveUser = useState();
    useEffect(() => {
        //user && localStorage.setItem(user,[]);
        let arr = JSON.parse(localStorage.getItem(user));
        console.log(arr);
        localStorage.setItem(user, JSON.stringify(tasks))
    }, [tasks, users])
    console.log(task, tasks)
    return (
        <div className="main">
            <CustomInput     
                value={user} 
                change={setUser} 
                save={saveUser}
                tasks= {users}/>   

            <CustomInput     
                value={task} 
                change={setTask} 
                save={saveTask}
                tasks= {tasks}/> 

        </div>
    )
}



export default Main;

// CustomInput.tsx       // Форма для добавления новой задачи
