import React, { useEffect } from "react";
import {useState}  from "react";
import CustomInput from "./components/CustomInput/index";
import List from "./components/List/index";
import UsernameInput from "./components/UsernameInput/index";

import './App.css';

const Main = () => {
    const [tasks, setTasks] = useState([]);
    const [user, setUser] = useState([]);
    const [currentUser, setCurrentUser] = useState();
    const inputnameUser = (username) => {
            setCurrentUser(username)
        if (!user.includes(username)) {
            setUser([...user, username])
            //ocalStorage.setItem(user, [])
        } else {
            let taskUser = JSON.parse(localStorage.getItem(username))
            //setTasks(taskUser);
            console.log(username, 'taskk', tasks,  'i eto', taskUser)
        }

    }

    const removeTask = (delTask) => {
        setTasks([...tasks.filter((el) => el.id !== delTask)])
    }
    const toggleTask = (id) => {
        setTasks([
            ...tasks.map((el) => 
                el.id === id ? {...el, check: !el.check} : {...el})])
    }

    useEffect(() => {
        //user && localStorage.setItem(user,[]);
        // let arr = JSON.parse(localStorage.getItem(user));
        // console.log(arr);
        // arr.push({tasks});
        if (user.includes(currentUser)) {
            setTasks(JSON.parse(localStorage.getItem(currentUser)))
        } else {
            //localStorage.setItem(user[user.length-1], "[]")
        }
        // setTasks(JSON.parse(localStorage.getItem(currentUser)))
        // localStorage.setItem(user[user.length-1], "[]")

    }, [user, currentUser]);


    return (
        <div className="main">
            <UsernameInput 

                inputnameUser = {inputnameUser}
            />
            <h1>ToDoList: {tasks.length && tasks.length}</h1>
            <CustomInput     
                tasks={tasks}
                setTasks = {setTasks}
                currentUser = {currentUser}
                />   
                {tasks.map((el) => {
                console.log(el)
                return (
                    <List 
                        todo={el}
                        key={el.id}
                        removeTask={removeTask}
                        toggleTask = {toggleTask}
                    />
                )
            })}


        </div>
    )
}



export default Main;

// CustomInput.tsx       // Форма для добавления новой задачи
