import React from "react";


import './App.css';

class Main extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            likes: 0
        }
        this.changeLikes = this.changeLikes.bind(this)
    }
    changeLikes(){
        this.state({
            likes: ++this.state.likes
        })
    }
    render() {
        const {likes} = this.state
        return (
            <div>
                <p>{likes}</p>
                <button onClick={() => this.changeLikes}>touch me!</button>
            </div>
        )
    }
}

export default Main;
