import React, {useEffect} from "react";
import {useState}  from "react";
//import {useState}  from "react";
import './index.css'

const CustomInput = ({tasks, setTasks, currentUser}) => {
    const [userInput, setUserInput] = useState('');
    //input task
    const inputTask = ({userInput}) => {
        if (!userInput == '') {
            saveTask({userInput});
            setUserInput('')
        }

    }
    //save new  task
    const saveTask = (userInput) => {
        if (userInput) {
            const newTask = {
                id: Math.random().toString(36).substr(2,9),
                task: userInput.userInput,
                check: false
            }
            setTasks([...tasks, newTask])
        }
    }
    useEffect(() => {
        localStorage.setItem(currentUser, JSON.stringify(tasks))
    }, [tasks])

    return (
        <div className="input-block">
            <input type="text" value={userInput} onChange={(e) => setUserInput(e.target.value)} />
            <button onClick={() => inputTask({userInput})}>ADD</button>
        </div>
    )
}
export default CustomInput;
