import { findByLabelText } from "@testing-library/react";
import React from "react";

import './index.css'

const List = ({todo, removeTask, toggleTask}) => {
    return (
        <div className={todo.check ? "item-todo done" : "item-todo"} key={todo.id}>
            <div>{todo.task}</div>
            <div onClick={() => toggleTask(todo.id)} className="item-done">OK</div>
            <div onClick={() => removeTask(todo.id)} className="item-delete">X</div>
        </div>
    )
}
export default List;
